('nuhome', 'launch', 'nuhome', 'Home', '', '', '', 0, 0, '', '\n\nnuAttachButtonImage(\'run_php\',\'RP\');\nnuAttachButtonImage(\'run_setup\',\'SET\');\nnuAttachButtonImage(\'form_button\',\'F\');\nnuAttachButtonImage(\'run_lang\',\'TR\');\nnuAttachButtonImage(\'run_report\',\'RR\');\nnuAttachButtonImage(\'user_home\',\'UH\');\nnuAttachButtonImage(\'run_user\',\'U\');\nnuAttachButtonImage(\'run_file\',\'FILE\');\nnuAttachButtonImage(\'edit_php\',\'P\');\nnuAttachButtonImage(\'edit_report\',\'R\');\nnuAttachButtonImage(\'run_access\',\'KEY\');\nnuAttachButtonImage(\'run_fast_form\',\'FF\');\nnuAttachButtonImage(\'run_format\',\'FMT\');\nnuAttachButtonImage(\'run_fast_report\',\'FR\');\nnuAttachButtonImage(\'object_button\',\'OB\');\nnuAttachButtonImage(\'run_sql\',\'SQL\');\nnuAttachButtonImage(\'run_chart\',\'CH\');\nnuAttachButtonImage(\'open_database\',\'DB\');\nnuAttachButtonImage(\'system_update\',\'SU\');\n\n\n$(\'.nuActionButton\').hide();\n\n$(\'#user_home\').addClass(\'nuUserHomeButton\');\n\n$(\'#open_database\')\n.attr(\'title\', \'PHPMyAdmin\')\n//.click(function(){\n//    window.open(\'nupmalogin.php\');\n//});\n\n\n$(\'#run_php\')\n.css(\'text-align\', \'left\')\n.css(\'padding\', \'0px 0px 0px 35px\')\n.css(\'background-size\', \'30px\')\n.css(\'background-repeat\', \'no-repeat\');\n\nnuGetStartingTab();\n'),
('nubrowse', 'subform', 'nubrowse', 'Browse Columns', 'zzzzsys_browse', 'zzzzsys_browse_id', '', 0, 5, 'SELECT * FROM zzzzsys_browse\nORDER BY sbr_order\n', '$(\'#nuSearchButton\').remove();\n$(\'#nuSearchField\').remove();\n$(\'#nuPrintButton\').remove();\n\nnuSetTitle($(\'#sbr_title\').val());\n\nvar pid = parent.nuFORM.getCurrent().record_id;\n\nif(nuFORM.getCurrent().record_id == -1){\n    $(\'#sbr_zzzzsys_form_id\').val(pid).change();\n}\n'),
